package com.randy.hive.udf;

import java.nio.charset.Charset;
import  org.apache.commons.codec.binary.Base64;
import org.apache.commons.lang.StringUtils;
import org.apache.hadoop.hive.ql.exec.UDF;
import org.apache.hadoop.io.Text;

/**
 * @author : RandySun (sunfeng152157@sina.com)
 * @date : 2020-04-09  10:35
 * Comment :
 */
public class decipher_installed_pgk extends UDF {


    private static final String DEFAULT_KEY = "mine";


    public static void main(String[] args) {
        String result = decodeXOR("");
        System.out.println(result);
    }

    public Text evaluate(Text str){
        // input parameter validate
        if(null == str || StringUtils.isBlank(str.toString())){
            return null ;
        }

        // lower
        return new Text(decodeXOR(str.toString())) ;
    }

    public Text evaluate(Text str,String passCode){
        // input parameter validate
        if(null == str || StringUtils.isBlank(str.toString())){
            return null ;
        }
        if(StringUtils.isEmpty(passCode)){
            return null;
        }

        return new Text(decodeXOR(str.toString(), passCode)) ;
    }

    public static String encodeXOR(String enc) {
        byte[] data = enc.getBytes(Charset.forName("UTF-8"));
        byte[] keyXORBytes = DEFAULT_KEY.getBytes(Charset.forName("UTF-8"));

        for (int i = 0, size = data.length; i < size; i++) {
            for (byte keyBytes : keyXORBytes) {
                data[i] = (byte) (data[i] ^ keyBytes);
            }
        }
        return new Base64().encodeToString(data);
    }

    public static String decodeXOR(String dec) {
        byte[] data = new Base64().decode(dec);
        byte[] keyXORBytes = DEFAULT_KEY.getBytes(Charset.forName("UTF-8"));
        for (int i = 0, size = data.length; i < size; i++) {
            for (byte keyBytes : keyXORBytes) {
                data[i] = (byte) (data[i] ^ keyBytes);
            }
        }
        return new String(data);
    }

    public static String decodeXOR(String dec, String passCode) {
        byte[] data = new Base64().decode(dec);
        byte[] keyXORBytes = keyXORBytes = passCode.getBytes(Charset.forName("UTF-8"));
        for (int i = 0, size = data.length; i < size; i++) {
            for (byte keyBytes : keyXORBytes) {
                data[i] = (byte) (data[i] ^ keyBytes);
            }
        }
        return new String(data);
    }

}
